<?phpnamespace User\Controller\Factory;use Interop\Container\ContainerInterface;use User\Controller\SliderController;use User\Service\SliderManager;use User\Thumb\Thumb;use Zend\ServiceManager\Factory\FactoryInterface;/** * This is the factory for PostController. Its purpose is to instantiate the * controller. */class SliderControllerFactory implements FactoryInterface{    public function __invoke(ContainerInterface $container, $requestedName, array $options = null)    {        $entityManager = $container->get('doctrine.entitymanager.orm_default');        $Manager = $container->get(SliderManager::class);        // Instantiate the controller and inject dependencies        return new SliderController($entityManager, $Manager);    }}