<?phpnamespace User\Entity;use Doctrine\ORM\Mapping as ORM;use Zend\Hydrator;/** * This class represents a single post in a blog. * @ORM\Entity * @ORM\Table(name="slider") * @ORM\HasLifecycleCallbacks */class Slider{    // Post status constants.    const STATUS_ACTIVE       = 1; // ativo user.    const STATUS_RETIRED      = 2; // inativo user.    /**     * @ORM\Id     * @ORM\Column(name="id")     * @ORM\GeneratedValue     */    protected $id;    /**     * @ORM\Column(name="titulo")     */    protected $titulo;    /**     * @ORM\Column(name="slug")     */    protected $slug;    /**     * @ORM\Column(name="status")     */    protected $status;    /**     * @ORM\Column(name="file")     */    protected $file;    /**     * @ORM\Column(name="url")     */    protected $url;    public function __construct($options = [])    {        (new Hydrator\ClassMethods())->hydrate($options, $this);    }    /**     * Returns ID of this post.     * @return integer     */    public function getId()    {        return $this->id;    }    /**     * Sets ID of this post.     * @param int $id     */    public function setId($id)    {        $this->id = $id;    }    /**     * @return mixed     */    public function getSlug()    {        return $this->slug;    }    /**     * @param mixed $slug     * @return Slider     */    public function setSlug($slug)    {        $this->slug = $slug;        return $this;    }    /**     * @return mixed     */    public function getFile()    {        return $this->file;    }    /**     * @param mixed $file     * @return Slider     */    public function setFile($file)    {        $this->file = $file;        return $this;    }    /**     * @return mixed     */    public function getTitulo()    {        return $this->titulo;    }    /**     * @param mixed $titulo     * @return Slider     */    public function setTitulo($titulo)    {        $this->titulo = $titulo;        return $this;    }    /**     * Returns status.     * @return integer     */    public function getStatus()    {        return $this->status;    }    /**     * Sets status.     * @param integer $status     */    public function setStatus($status)    {        $this->status = $status;    }    /**     * Returns possible statuses as array.     * @return array     */    public static function getStatusList()    {        return [            self::STATUS_ACTIVE => 'Ativo',            self::STATUS_RETIRED => 'Inativo'        ];    }    /**     * Returns user status as string.     * @return string     */    public function getStatusAsString()    {        $list = self::getStatusList();        if (isset($list[$this->status]))            return $list[$this->status];        return 'Unknown';    }    /**     * @return mixed     */    public function getUrl()    {        return $this->url;    }    /**     * @param mixed $url     * @return Slider     */    public function setUrl($url)    {        $this->url = $url;        return $this;    }    public function toArray(){        return [            'id'                => $this->getId(),            'titulo'            => $this->getTitulo(),            'slug'              => $this->getSlug(),            'status'            => $this->getStatus(),            'file'              => $this->getFile(),            'url'               => $this->getUrl()       ];    }}