<?phpnamespace User\Thumb;class Thumb{    protected $thumb;    public function __construct($thumb)    {        $this->thumb = $thumb;    }    public function geraThumb($nomeImg)    {        // gerar thumb **/        //--admin--        $twith1x = "50";        $heigth1y = "50";        $twith2x = "768";        $heigth2y = "547";        //----        $twith3x = "361";        $heigth3y = "360";        $twith4x = "585";        $heigth4y = "585";        $twith5x = "120";        $heigth5y = "120";        $twith6x = "1280";        $heigth6y = "500";        $twith7x = "200";        $heigth7y = "200";        $twith8x = "447";        $heigth8y = "447";        $twith9x = "640";        $heigth9y = "480";        $path = 'public/media/'.$nomeImg;        //--tamanho--1--        $thumb1         = $this->thumb;        $thumbnail1     = $thumb1->create($path, $options = [], $plugins = []);        $thumbnail1->adaptiveResize( $twith1x, $heigth1y);        $thumbnail1->save('public/media/thumb/'. $twith1x.'x'.$heigth1y.'-'.$nomeImg);        //--tamanho--2--        $thumb2         = $this->thumb;        $thumbnail2     = $thumb2->create($path, $options = [], $plugins = []);        $thumbnail2->adaptiveResize( $twith2x,  $heigth2y);        $thumbnail2->save('public/media/thumb/'. $twith2x.'x'.$heigth2y.'-'.$nomeImg);        //--tamanho--3--        $thumb3         = $this->thumb;        $thumbnail3     = $thumb3->create($path, $options = [], $plugins = []);        $thumbnail3->adaptiveResize( $twith3x,  $heigth3y);        $thumbnail3->save('public/media/thumb/'. $twith3x.'x'.$heigth3y.'-'.$nomeImg);        //--tamanho--50x50--        $thumb4         = $this->thumb;        $thumbnail4     = $thumb4->create($path, $options = [], $plugins = []);        $thumbnail4->adaptiveResize( $twith4x,  $heigth4y);        $thumbnail4->save('public/media/thumb/'. $twith4x.'x'.$heigth4y.'-'.$nomeImg);        //--tamanho--768x547--        $thumb5         = $this->thumb;        $thumbnail5     = $thumb5->create($path, $options = [], $plugins = []);        $thumbnail5->adaptiveResize( $twith5x,  $heigth5y);        $thumbnail5->save('public/media/thumb/'. $twith5x.'x'.$heigth5y.'-'.$nomeImg);        //--tamanho--4--        $thumb6         = $this->thumb;        $thumbnail6     = $thumb6->create($path, $options = [], $plugins = []);        $thumbnail6->adaptiveResize( $twith6x,  $heigth6y);        $thumbnail6->save('public/media/thumb/'. $twith6x.'x'.$heigth6y.'-'.$nomeImg);        $thumb7         = $this->thumb;        $thumbnail7     = $thumb7->create($path, $options = [], $plugins = []);        $thumbnail7->adaptiveResize( $twith7x,  $heigth7y);        $thumbnail7->save('public/media/thumb/'. $twith7x.'x'.$heigth7y.'-'.$nomeImg);        $thumb8         = $this->thumb;        $thumbnail8     = $thumb8->create($path, $options = [], $plugins = []);        $thumbnail8->adaptiveResize( $twith8x,  $heigth8y);        $thumbnail8->save('public/media/thumb/'. $twith8x.'x'.$heigth8y.'-'.$nomeImg);        $thumb9         = $this->thumb;        $thumbnail9     = $thumb9->create($path, $options = [], $plugins = []);        $thumbnail9->adaptiveResize( $twith9x,  $heigth9y);        $thumbnail9->save('public/media/thumb/'. $twith9x.'x'.$heigth9y.'-'.$nomeImg);        return $this;    }    public function removeThumb($nomeImg)    {        //--admin--        $twith1x = "50";        $heigth1y = "50";        $twith2x = "768";        $heigth2y = "547";        //----        $twith3x = "361";        $heigth3y = "360";        $twith4x = "585";        $heigth4y = "585";        $twith5x = "120";        $heigth5y = "120";        $twith6x = "1280";        $heigth6y = "500";        $twith7x = "200";        $heigth7y = "200";        $twith8x = "447";        $heigth8y = "447";        $twith9x = "640";        $heigth9y = "480";        unlink('public/media/thumb/'. $twith1x.'x'.$heigth1y.'-'.$nomeImg);        unlink('public/media/thumb/'. $twith2x.'x'.$heigth2y.'-'.$nomeImg);        unlink('public/media/thumb/'. $twith3x.'x'.$heigth3y.'-'.$nomeImg);        unlink('public/media/thumb/'. $twith4x.'x'.$heigth4y.'-'.$nomeImg);        unlink('public/media/thumb/'. $twith5x.'x'.$heigth5y.'-'.$nomeImg);        unlink('public/media/thumb/'. $twith6x.'x'.$heigth6y.'-'.$nomeImg);        unlink('public/media/thumb/'. $twith7x.'x'.$heigth7y.'-'.$nomeImg);        unlink('public/media/thumb/'. $twith8x.'x'.$heigth8y.'-'.$nomeImg);        unlink('public/media/thumb/'. $twith9x.'x'.$heigth9y.'-'.$nomeImg);    }}